import React, { useEffect } from 'react';
import { useParams } from "react-router-dom";
import { ReactFlvPlayer } from "react-flv-player";
import { Chat } from "./Chat";
import { ChannelDescription } from './ChannelDescription';
import { useChannelDetails } from '../../../shared/hooks';
import { LoadingSpinner } from "../../../shared/components";

export const Stream = () => {
    return <div className='channel-video-container'>
        <ReactFlvPlayer 
            width='100%'
            height='100%'
            url='http://localhost:8000/live/f8e18289-b64a-4b30-a578-605a5559f864.flv'
        />
    </div>
};

export const ChannelView = (getChannels) => {
    const { isFetching, getChannelDetails, channelDetails } = useChannelDetails();
    
    const { id } = useParams();

    useEffect(() => {
        getChannelDetails(id);
    }, []);

    if (isFetching) {
        return <LoadingSpinner />;
    }

    return (
        <div className='channel-container'>
            <div className='channel-video-description-section'>
                {/* <div className="channel-offline-placeholder">
                    <span>Channel is offline</span>
                </div> */}
                <Stream  />
                <ChannelDescription 
                    channelId={channelDetails.id}
                    title={channelDetails.title}
                    description={channelDetails.description}
                    username={channelDetails.username}
                    getChannels={getChannels}
                />
            </div>
            <Chat />
        </div>
    );
};
