import React, { useState } from 'react';
import { Logo } from './Logo';
import { AuthInput } from './AuthInput';

export const Login = ({ switchAuthHandler }) => {
    const [formState, setIsLogin] = useState({
        email: {
            value: "",
            isValid: false,
            showError: false,
        },
        password: {
            value: "",
            isValid: false,
            showError: false,
        },
    });

    return <div className='login-container'>
        <Logo text={"Log in to clone"} />
        <form className='auth-form'>
            <AuthInput field="email" label="Email" value={formState.email.value} />
            <AuthInput field="password" label="Password" value={formState} />
            <button type="">Log in</button>
        </form>
        <span onClick={switchAuthHandler} className='auth-form-switch-label'>Don 't have an account ? Sign up</span>
    </div>;
};
